import{_ as t,o as e,c as o,Q as a}from"./chunks/framework.19358895.js";const P=JSON.parse('{"title":"初始HTTP协议","description":"","frontmatter":{},"headers":[],"relativePath":"program/frontend/Node/chapter1/32.初识HTTP协议.md","filePath":"program/frontend/Node/chapter1/32.初识HTTP协议.md","lastUpdated":1697287289000}'),r={name:"program/frontend/Node/chapter1/32.初识HTTP协议.md"},p=a('<h1 id="初始http协议" tabindex="-1">初始HTTP协议 <a class="header-anchor" href="#初始http协议" aria-label="Permalink to &quot;初始HTTP协议&quot;">​</a></h1><p>HTTP（hypertext transport protocol）协议；中文叫超文本传输协议，是一种基于TCP/IP的应用层通信协议。</p><blockquote><p>HTTP协议是互联网应用最广泛的协议之一，无论是客户端开发还是服务端开发，都是必须要掌握的技能之一，十分重要</p></blockquote><p>协议是<strong>双方必须共同遵从的一组约定。</strong></p><blockquote><p>协议是一种约定；协议有约束主体；</p></blockquote><p>HTTP协议详细规定了 浏览器 和万维网 服务器 之间互相通信的规则。</p><p>协议中主要规定了两个方面的内容：</p><ul><li>客户端：用来向服务器发送数据，这个行为被称为请求，发送的请求内容称为请求报文</li><li>服务端：向客户端返回数据，这个行为被称之为响应，响应的结果内容被称之为响应报文</li></ul>',8),_=[p];function n(c,s,T,l,d,i){return e(),o("div",null,_)}const m=t(r,[["render",n]]);export{P as __pageData,m as default};
