import{_ as e,o as t,c as o,Q as a}from"./chunks/framework.419948d5.js";const u=JSON.parse('{"title":"网页资源加载基本过程","description":"","frontmatter":{},"headers":[],"relativePath":"program/frontend/Node/chapter1/53.网页资源加载基本过程.md","filePath":"program/frontend/Node/chapter1/53.网页资源加载基本过程.md","lastUpdated":1697287289000}'),r={name:"program/frontend/Node/chapter1/53.网页资源加载基本过程.md"},_=a('<h1 id="网页资源加载基本过程" tabindex="-1">网页资源加载基本过程 <a class="header-anchor" href="#网页资源加载基本过程" aria-label="Permalink to &quot;网页资源加载基本过程&quot;">​</a></h1><p>一个网页在加载过程中一共向服务器发送几次请求？</p><p>网页资源的加载都是循序渐进的：</p><ul><li><p>首先获取 HTML 的内容</p></li><li><p>然后解析 HTML 在发送其他资源的请求，如 CSS，Javascript，图片等。 理解了这个内容对于后续的学习与成长有非常大的帮助</p><blockquote><p>默认情况下，资源请求的顺序按照HTML文档解析的顺序执行，一些会发送请求的情况：</p><ul><li>form</li><li>img</li><li>script</li><li>link</li></ul><p>这些请求是并行的，也就是不会阻塞，不会等待之前的资源请求回来再去请求，而是解析到了对应的标签就会发送对应的请求</p></blockquote></li></ul><blockquote><p>favicon.ico 请求是chromn浏览器的默认行为，用来获取标签页的图标（icon）</p><p>ws是vscode插件发送的请求，用来实现网页的自动刷新功能</p></blockquote>',5),i=[_];function p(c,l,n,s,d,m){return t(),o("div",null,i)}const f=e(r,[["render",p]]);export{u as __pageData,f as default};
